import React from "react";
import "./ExpenseForm.css";

function ExpenseForm() {
  const expenseInputChangeHandler = (event) => {
    console.log(event.target.value)
    return;
  };

  const dateInputChangeHandler = (event) => {
    console.log(event.target.value)
    return;
  };

  const amountInputChangeHandler = (event) => {
    console.log(event.target.value)
    return;
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" placeholder="Add Expense" onChange={expenseInputChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" placeholder="Add Date" onChange={dateInputChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" placeholder="Add Amount" onChange={amountInputChangeHandler}/>
        </div>
        <div>
          <button className="new-expense__actions" type="submit">Submit</button>
        </div>
      </div>
    </form>
  );
}

export default ExpenseForm;
