import React from "react";
import "./ExpenseItem.css";

function ExpenseItem({ expenses, setExpenses }) {
    setExpenses(prev => {
        [...prev, {id: 'e6', title: 'A', amount: 12, date: new Date(2021, 2, 28)}]
    })
  return expenses.map((expense) => {
    const day = expense.date.toLocaleString("en-US", { day: "2-digit" });
    const month = expense.date.toLocaleString("en-US", { month: "long" });
    const year = expense.date.getFullYear();

    return (
      <div className="expense-item">
        <div className="expense-item__item">
          <div>{month}</div>
          <div>{day}</div>
          <div>{year}</div>
        </div>
        <div className="expense-item__description">
          <h2>{expense.title}</h2>
          <div className="expense-item__price">{expense.amount}</div>
        </div>
      </div>
    );
  });
}

export default ExpenseItem;
